const progressBar=document.querySelector("#progress-bar"),scrollToTop=document.querySelector("#scroll-to-top"),scrollContainer=()=>document.documentElement||document.body;document.addEventListener("scroll",()=>{const e=scrollContainer().scrollTop/(scrollContainer().scrollHeight-scrollContainer().clientHeight)*100;progressBar.style.width=`${e}%`,scrollContainer().scrollTop>100?(progressBar.classList.remove("hidden"),scrollToTop.classList.remove("hidden")):(progressBar.classList.add("hidden"),scrollToTop.classList.add("hidden"))}),scrollToTop.addEventListener("click",()=>{document.body.scrollIntoView({behavior:"smooth"})})